E2Helper.Descriptions["rangerOffsetManual(v,v,r)"] = "Returns direct table result from a util.traceLine call with startpos, endpos and filter. [Exclusive]"
E2Helper.Descriptions["rangerSetFilter(r)"] = "Sets the current filter to be used for builtin e2 rangers like with rangerOffset(v,v) [Exclusive]"
E2Helper.Descriptions["canPrintGlobal()"] = "Returns 1 or 0 for whether you can call printGlobal(). [Exclusive]"
E2Helper.Descriptions["printGlobal(...)"] = "Print to EVERYONE on the server, with colors and stuff. [Exclusive]"
E2Helper.Descriptions["printGlobal(r,...)"] = "Print to everyone on the array with color and other nice things. [Exclusive]"
E2Helper.Descriptions["printGlobalClk()"] = "Returns 1 or 0 for if the e2 was triggered by a printGlobal event. [Exclusive]"
E2Helper.Descriptions["runOnPrintGlobal(n)"] = "Set your e2 to run when others use printGlboal. [Exclusive]"
E2Helper.Descriptions["lastGPrintRaw()"] = "Returns vector colors and text from the last global print. [Exclusive]"
E2Helper.Descriptions["lastGPrintRaw(e)"] = "Returns vector colors and text from the last global print from player e. [Exclusive]"
E2Helper.Descriptions["lastGPrintSender()"] = "Returns the last player to send a globalprint. [Exclusive]"
E2Helper.Descriptions["lastGPrintText()"] = "Returns the last printGlobal with the sifted out text. [Exclusive]"
E2Helper.Descriptions["lastGPrintText(e)"] = "Returns the last printGlobal from player E with the sifted out text. [Exclusive]"
E2Helper.Descriptions["hideChatPly(e)"] = "Hides the last message (e) sent to the chat and notifies them. [Exclusive]"
E2Helper.Descriptions["canHideChatPly(e)"] = "Returns 1 or 0 for whether you can hide someone's chat. [Exclusive]"
